// main.jsx
import React, { useState } from "react";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";
import "../styles/main.css";

const Main = () => {
  const [formData, setFormData] = useState({
    name: "",
    area: "",
    landOptions: "",
  });
  const [predictedPrice, setPredictedPrice] = useState(null);
  const [error, setError] = useState(null); // New state for error messages
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
    setError(null); // Clear error when user modifies form
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setPredictedPrice(null); // Clear previous results
    setError(null); // Clear previous errors

    // Validate form data
    if (!formData.name || !formData.area || !formData.landOptions) {
      setError("Please fill in all fields to get a price estimation.");
      return;
    }

    try {
      const response = await axios.post("http://localhost:5000/api/predict-price", formData);
      setPredictedPrice(response.data.price);
    } catch (err) {
      console.error("Error predicting price:", err);
      setError("An error occurred while fetching the price. Please try again.");
    }
  };

  const viewRelatedDesigns = (price) => {
    const searchParams = {
      name: formData.name,
      area: formData.area,
      landOptions: formData.landOptions,
      price,
    };
    navigate("/home", { state: searchParams });
  };

  return (
    <div className="col-sm-12 col-md-12 col-lg-12 col-xl-12">
      <div className="sections hero-sec col-sm-12 col-md-12 col-lg-12 col-xl-12">
        <h1 className="text-center">Welcome to Opex Home Solutions</h1>
        <div className="container">
          <div className="row">
            <div className="col-sm-12 col-md-12 col-lg-8 col-xl-8">
              <p className="text-center hero-txt">
                A complete solution for planning and designing your dream home
              </p>
            </div>
            <div className="col-sm-12 col-md-12 col-lg-4 col-xl-4 p-4 border rounded est-box">
              <h2 className="text-center mb-4">Get a Quick Price Estimation</h2>
              <form onSubmit={handleSubmit} className="row g-3">
                <div className="col-12">
                  <label className="form-label">House Design Name:</label>
                  <select
                    name="name"
                    className="form-select"
                    value={formData.name}
                    onChange={handleChange}
                    required
                  >
                    <option value="">Select</option>
                    <option value="Bungalow">Bungalow</option>
                    <option value="Villa">Villa</option>
                    <option value="Apartment">Apartment</option>
                  </select>
                </div>

                <div className="col-md-6">
                  <label className="form-label">Area (in sqft):</label>
                  <select
                    name="area"
                    className="form-select"
                    value={formData.area}
                    onChange={handleChange}
                    required
                  >
                    <option value="">Select</option>
                    <option value="500-1000">500-1000</option>
                    <option value="1000-1500">1000-1500</option>
                    <option value="1500-2000">1500-2000</option>
                    <option value="2000-2500">2000-2500</option>
                    <option value="2500-3000">2500-3000</option>
                    <option value="3000-3500">3000-3500</option>
                    <option value="3500-4000">3500-4000</option>
                    <option value="4000-6000">4000-6000</option>
                    <option value="6000-10000">6000-10000</option>
                  </select>
                </div>

                <div className="col-md-6">
                  <label className="form-label">Land Options:</label>
                  <select
                    name="landOptions"
                    className="form-select"
                    value={formData.landOptions}
                    onChange={handleChange}
                    required
                  >
                    <option value="">Select</option>
                    <option value="Urban">Urban</option>
                    <option value="Suburban">Suburban</option>
                    <option value="Rural">Rural</option>
                  </select>
                </div>

                <div className="col-12 mt-4 text-center">
                  <button
                    type="submit"
                    className="btn btn-lg btn-outline-warning"
                  >
                    Get Price Estimation
                  </button>
                </div>
              </form>

              {error && (
                <div className="alert alert-danger mt-4 text-center">
                  {error}
                </div>
              )}

              {predictedPrice && (
                <div className="result mt-4 text-center">
                  <h3 className="">
                    Estimated Price: ₹{predictedPrice.toFixed(2)} lakhs
                  </h3>
                  <button
                    className="btn btn-lg btn-outline-success"
                    onClick={() => viewRelatedDesigns(predictedPrice)}
                  >
                    Explore the Designs
                  </button>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Main;
