// main.jsx
import React, { useState } from "react";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";
import "../styles/main.css";

const Main = () => {
  const [formData, setFormData] = useState({
    name: "",
    area: "",
    landOptions: "",
  });
  const [predictedPrice, setPredictedPrice] = useState(null);
  const [isLoading, setIsLoading] = useState(false); // Loading state
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true); // Start loading
    try {
      const response = await axios.post("/api/predict-price", formData);
      setPredictedPrice(response.data.price);
    } catch (error) {
      console.error("Error predicting price:", error);
    } finally {
      setIsLoading(false); // Stop loading
    }
  };

  const viewRelatedDesigns = (price) => {
    const searchParams = {
      name: formData.name,
      area: formData.area,
      landOptions: formData.landOptions,
      price,
    };
    navigate("/home", { state: searchParams });
  };

  return (
    <div className="col-sm-12 col-md-12 col-lg-12 col-xl-12">
      <div className="sections hero-sec col-sm-12 col-md-12 col-lg-12 col-xl-12">
        <h1 className="text-center">Welcome to Opex Home Solutions</h1>
        <div className="container">
          <div className="row">
            <div className="col-sm-12 col-md-12 col-lg-8 col-xl-8">
              <p className="text-center hero-txt">
                A complete solution for planning and designing your dream home
              </p>
            </div>
            <div className="col-sm-12 col-md-12 col-lg-4 col-xl-4 p-4 border rounded est-box">
              <h2 className="text-center mb-4">Get a Quick Price Estimation</h2>
              <form onSubmit={handleSubmit} className="row g-3">
                <div className="col-12">
                  <label className="form-label">House Design Name:</label>
                  <select
                    name="name"
                    className="form-select"
                    value={formData.name}
                    onChange={handleChange}
                    required
                  >
                    <option value="">Select</option>
                    <option value="Bungalow">Bungalow</option>
                    <option value="Villa">Villa</option>
                    <option value="Apartment">Apartment</option>
                  </select>
                </div>

                <div className="col-md-6">
                  <label className="form-label">Area (in sqft):</label>
                  <select
                    name="area"
                    className="form-select"
                    value={formData.area}
                    onChange={handleChange}
                    required
                  >
                    <option value="">Select</option>
                    <option value="500-1000">500-1000</option>
                    <option value="1000-1500">1000-1500</option>
                    <option value="1500-2000">1500-2000</option>
                    <option value="2000-2500">2000-2500</option>
                    <option value="2500-3000">2500-3000</option>
                    <option value="3000-3500">3000-3500</option>
                    <option value="3500-4000">3500-4000</option>
                    <option value="4000-6000">4000-6000</option>
                    <option value="6000-10000">6000-10000</option>
                  </select>
                </div>

                <div className="col-md-6">
                  <label className="form-label">Land Options:</label>
                  <select
                    name="landOptions"
                    className="form-select"
                    value={formData.landOptions}
                    onChange={handleChange}
                    required
                  >
                    <option value="">Select</option>
                    <option value="Urban">Urban</option>
                    <option value="Suburban">Suburban</option>
                    <option value="Rural">Rural</option>
                  </select>
                </div>

                <div className="col-12 mt-4 text-center">
                  <button
                    type="submit"
                    className="btn btn-lg btn-outline-warning"
                    disabled={isLoading} // Disable button while loading
                  >
                    {isLoading ? "Calculating..." : "Get Price Estimation"}
                  </button>
                </div>
              </form>

              {isLoading && (
                <div className="text-center mt-3">
                  <div className="spinner-border text-warning" role="status">
                    <span className="visually-hidden">Loading...</span>
                  </div>
                </div>
              )}

              {predictedPrice && !isLoading && (
                <div className="result mt-4 text-center">
                  <h3 className="">
                    Estimated Price: â‚¹{predictedPrice.toFixed(2)} lakhs
                  </h3>
                  <button
                    className="btn btn-lg btn-outline-success"
                    onClick={() => viewRelatedDesigns(predictedPrice)}
                  >
                    Explore the Designs
                  </button>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>

{/* Offerings Section */}
<div className="sections col-12 p-0">
  <div className="text-center py-5">
    <h1 className="section-txt m-0">What We Offer</h1>
    <p className="section-subtxt">Your one-stop solution for home designs, interiors, and architecture.</p>
  </div>
  <div className="row justify-content-center">
    {[
      {
        title: "Home Designs",
        description: "Discover diverse home designs tailored to various styles and budgets. Customize layouts and get detailed plans with cost estimates.",
        img: "https://media-assets0.s3.ap-south-1.amazonaws.com/ext_int_plan.png",
        link: "/designs",
      },
      {
        title: "Exterior Designs",
        description: "Comprehensive exterior home designs with detailed price, area specifications, and estimated construction costs for various styles.",
        img: "https://thearchitectsdiary.com/wp-content/uploads/2020/05/MEtal-Facade.jpg",
        link: "/exteriors",
      },
      {
        title: "Interior Designs",
        description: "Personalize your spaces with customizable furniture layouts and decor styles. Choose from modern, traditional, or minimalist themes.",
        img: "https://asset-ng.skoiy.com/9b80a6f781ff336f/yrwwqpnyb7ys.jpg?w=970&q=90&fm=webp",
        link: "/interiors",
      },
      {
        title: "Architectural Plans",
        description: "Get access to architectural plans that align with modern design principles and integrate seamlessly with existing infrastructure.",
        img: "https://house-designs-data.s3.ap-south-1.amazonaws.com/Building_10003/plan/Building_10003_pln_1.jpg",
        link: "/architecture",
      },
    ].map((item, index) => (
      <div
        className="col-sm-12 col-md-6 col-lg-4 my-3"
        data-aos="fade-up"
        key={index}
      >
        <div className="card h-100 shadow border-0">
          <div className="overflow-hidden">
            <img
              src={item.img}
              alt={item.title}
              className="card-img-top img-fluid"
              style={{ transition: "transform 0.5s" }}
            />
          </div>
          <div className="card-body d-flex flex-column">
            <h5 className="card-title text-center">{item.title}</h5>
            <p className="card-text">{item.description}</p>
            <div className="mt-auto text-center">
              <Link to={item.link} className="btn btn-primary btn-lg">
                Explore Designs
              </Link>
            </div>
          </div>
        </div>
      </div>
    ))}
